import React, { Component } from 'react';
import axios from 'axios';

const Task = props => {
    <div>
        {props.task.title}
    </div>
    // <div className="card">
    //     <div className="card-body">
    //         <h5 className="card-title">{props.task.title}</h5>
    //         <h6 className="card-subtitle mb-2 text-muted">{props.task.course}</h6>
    //         <h6 className="card-subtitle mb-2 text-muted">{props.task.deadline.substring(0,10)}</h6>
    //         <p className="card-text">{props.task.description}</p>
    //     </div>
    // </div>
}

export default class ActiveTasksList extends Component {
    constructor(props){
        super(props);

        this.state = {
            isLoaded: false,
            tasks: []
        };
    }


    componentDidMount(){
        axios.get('http://localhost:5000/tasks/')
            .then(response => {
                this.setState({
                    isLoaded: true,
                    tasks: response.data 
                });
            })
            .catch(err => {
                console.log(err);
            });
    }

    taskList(){
        return this.state.tasks.map(currentTask => {
            return <Task task={currentTask} key={currentTask._id} />;
        });
    }

    render(){
        const isLoaded = this.state.isLoaded;

        if(!isLoaded){
            return (
                <div className="container">
                    Loading...
                </div>
            );
        }

        return (
            <div className="container">
                <h3>Tasks</h3>
                {this.taskList()}
            </div>
        );
    }

}
