import React, { Component } from 'react';
import Modal from 'react-bootstrap/Modal';
import { Link } from 'react-router-dom';
import axios from 'axios';
import '../styles/custom.css';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";

const Task = props => {
    return (
            <div className="card mx-3 my-3 col-md-3">
                <div className="card-body">
                    <div className="row">
                        <div className="col-10">
                            <h5 className="card-title">{props.task.title}</h5>
                            <h6 className="card-subtitle mb-2 text-muted">{props.task.course}</h6>
                            <h6 className="card-subtitle mb-2 text-muted">{props.task.deadline.substring(0,10)}</h6>
                            <p className="card-text">{props.task.description}</p>
                            <a href="#" className="card-link" onClick={() => props.showModal(props.task._id)}>Edit</a>
                            <a href="#" className="card-link" onClick={() => {props.deleteTask(props.task._id)}}>Delete</a>
                        </div>
                        <div className="col-2">
                            <button type="button" className="btn btn-outline-success btn-lg">âœ“</button>
                        </div>
                    </div>
                </div>
            </div>
    );
}

export default class ActiveTasksList extends Component {
    constructor(props){
        super(props);

        this.state = {
            showModal: false,
            isLoaded: false,
            tasks: [],
            currentTaskTitle: '',
            currentTaskCourse: '',
            currentTaskDescription: '',
            currentTaskDeadline: new Date()
        };

        this.deleteTask = this.deleteTask.bind(this);
        this.toggleModal = this.toggleModal.bind(this);
        this.onChangeCurrentTaskTitle = this.onChangeCurrentTaskTitle.bind(this);
        this.onChangeCurrentTaskCourse = this.onChangeCurrentTaskCourse.bind(this);
        this.onChangeCurrentTaskDeadline = this.onChangeCurrentTaskDeadline.bind(this);
        this.onChangeCurrentTaskDescription = this.onChangeCurrentTaskDescription.bind(this);

        this.modalTitleRef = React.createRef();
        this.modalCourseRef = React.createRef();
        this.modalDeadlineRef = React.createRef();
        this.modalDescriptionRef = React.createRef();

    }

    componentDidMount(){

        this.modalTitleRef.current.value = this.state.currentTaskTitle;
        this.modalCourseRef.current.value = this.state.currentTaskCourse;
        this.modalDeadlineRef.current.selected = this.state.currentTaskDeadline;
        this.modalDescriptionRef.current.value = this.state.currentTaskDescription;

        axios.get('http://localhost:5000/tasks/')
            .then(response => {
                this.setState({
                    isLoaded: true,
                    tasks: response.data 
                });
            })
            .catch(err => {
                console.log(err);
            });
    }

    deleteTask(id){
        axios.delete('http://localhost:5000/tasks/'+id)
            .then(res => {
                console.log(res.data);
            })
            .catch(err => console.log(err));

        this.setState({
            tasks: this.state.tasks.filter(el => el._id !== id)
        });
    }

    toggleModal(id){

        if(id !== ''){
            this.setState({
                currentTaskTitle: this.state.tasks.find(task => task._id === id).title,
                currentTaskCourse: this.state.tasks.find(task => task._id === id).course,
                currentTaskDeadline: new Date(this.state.tasks.find(task => task._id === id).deadline),
                currentTaskDescription: this.state.tasks.find(task => task._id === id).description,
            });
        }

        this.setState({
            showModal: !this.state.showModal,
        });

    }

    onChangeCurrentTaskTitle(e){
        this.setState({
           currentTaskTitle: e.target.value 
        });
    }

    onChangeCurrentTaskCourse(e){
        this.setState({
            currentTaskCourse: e.target.value
        });
    }

    onChangeCurrentTaskDeadline(deadline){
        this.setState({
            currentTaskDeadline: deadline
        });
    }

    onChangeCurrentTaskDescription(e){
        this.setState({
            currentTaskDescription: e.target.value
        });
    }

    render(){
        const {isLoaded, tasks} = this.state;

        if(!isLoaded){
            return (
                <div className="container">
                    Loading...
                </div>
            );
        }

        return (
            <div className="main-container">
                <div className="row">
                    {tasks.map(currentTask => <Task key={currentTask._id} task={currentTask} deleteTask={this.deleteTask} showModal={this.toggleModal}/>)}
                </div>

                <Modal show={this.state.showModal} onHide={() => this.toggleModal('')} size="lg">
                    <form onSubmit={this.onEditTask}>
                        <Modal.Header>
                            <Modal.Title>
                                <div className="form-group">
                                    <label>Title: </label>
                                    <input type="text" ref={this.modalTitleRef}
                                        required
                                        className="form-control"
                                        value={this.state.currentTaskTitle}
                                        onChange={this.onChangeCurrentTaskTitle}
                                    />
                                </div>
                            </Modal.Title>
                        </Modal.Header>
                        <Modal.Body>
                            <div className="form-group">
                                <label>Course: </label>
                                <input type="text" ref={this.modalCourseRef}
                                    required
                                    className="form-control"
                                    value={this.state.currentTaskCourse}
                                    onChange={this.onChangeCurrentTaskCourse}
                                />
                            </div>
                            <div className="form-group">
                                <label>Deadline: </label>
                                <DatePicker
                                    ref={this.modalDeadlineRef}
                                    selected={this.state.currentTaskDeadline}
                                    onChange={this.onChangeCurrentTaskDeadline}
                                />
                            </div>
                            <div className="form-group">
                                <label>Description: </label>
                                <textarea
                                    ref={this.modalDescriptionRef}
                                    className="form-control"
                                    value={this.state.currentTaskDescription}
                                    onChange={this.onChangeCurrentTaskDescription}
                                />
                            </div>
                        </Modal.Body>
                        <Modal.Footer>
                            <button onClick={() => this.toggleModal('')}>Cancel</button>
                            <button>Save</button>
                        </Modal.Footer>
                    </form>
                </Modal>
            </div>
        );
    }

}
