import React, { Component } from 'react';
import Modal from 'react-bootstrap/Modal';
import { Link } from 'react-router-dom';
import axios from 'axios';
import '../styles/custom.css';

const Task = props => {
    const [isOpen, setIsOpen] = React.useState(false);

    const showModal = () => {
        setIsOpen(true);
    };

    const hideModal = () => {
        setIsOpen(false);
    };

    return (
        <div className="container">

            <div className="card mx-3 my-3 col-md-3">
                <div className="card-body">
                    <div className="row">
                        <div className="col-10">
                            <h5 className="card-title">{props.task.title}</h5>
                            <h6 className="card-subtitle mb-2 text-muted">{props.task.course}</h6>
                            <h6 className="card-subtitle mb-2 text-muted">{props.task.deadline.substring(0,10)}</h6>
                            <p className="card-text">{props.task.description}</p>
                            <a href="#" className="card-link" onClick={showModal}>Edit</a>
                            <a href="#" className="card-link" onClick={() => {props.deleteTask(props.task._id)}}>Delete</a>
                        </div>
                        <div className="col-2">
                            <button type="button" className="btn btn-outline-success btn-lg">âœ“</button>
                        </div>
                    </div>
                </div>
            </div>

            <Modal show={isOpen} onHide={hideModal}>
                <Modal.Header>
                    <Modal.Title>Hi</Modal.Title>
                </Modal.Header>
                <Modal.Body>The body</Modal.Body>
                <Modal.Footer>
                    <button onClick={hideModal}>Cancel</button>
                    <button>Save</button>
                </Modal.Footer>
            </Modal>

        </div>
    );
}

export default class ActiveTasksList extends Component {
    constructor(props){
        super(props);

        this.state = {
            isLoaded: false,
            tasks: []
        };

        this.deleteTask = this.deleteTask.bind(this);
    }

    componentDidMount(){
        axios.get('http://localhost:5000/tasks/')
            .then(response => {
                this.setState({
                    isLoaded: true,
                    tasks: response.data 
                });
            })
            .catch(err => {
                console.log(err);
            });
    }

    deleteTask(id){
        axios.delete('http://localhost:5000/tasks/'+id)
            .then(res => {
                console.log(res.data);
            })
            .catch(err => console.log(err));

        this.setState({
            tasks: this.state.tasks.filter(el => el._id !== id)
        });
    }

    render(){
        const {isLoaded, tasks} = this.state;

        if(!isLoaded){
            return (
                <div className="container">
                    Loading...
                </div>
            );
        }

        return (
            <div className="main-container">
                <div className="row">
                    {tasks.map(currentTask => <Task key={currentTask._id} task={currentTask} deleteTask={this.deleteTask}/>)}
                </div>
            </div>
        );
    }

}
