import React, { Component } from 'react';
import axios from 'axios';

const Task = props => {
    <div className="card">
        <div className="card-body">
            <h5 className="card-title">{props.task.title}</h5>
            <h6 className="card-subtitle mb-2 text-muted">{props.task.course}</h6>
            <h6 className="card-subtitle mb-2 text-muted">{props.task.deadline}</h6>
            <p className="card-text">{props.task.description}</p>
        </div>
    </div>
}

export default class ActiveTasksList extends Component {
    constructor(props){
        super(props);

        this.state = {tasks: []};
    }

    componentDidMount(){
        axios.get('http://localhost:5000/tasks/')
            .then(response => {
                this.setState({ tasks: response.data });
            })
            .catch(err => {
                console.log(err);
            })
    }

    tasksList(){
        return this.state.tasks.map(currentTask => {
            return <Task task={currentTask} />
        })
    }

    render(){
        return (
            <div className="container">
                { this.tasksList() }
            </div>
        );
    }

}
