import React, { Component } from 'react';
import axios from 'axios';
import '../styles/custom.css';

const Task = props => {
    return (
        <div className="card mx-3">
            <div className="card-body">
                <h5 className="card-title">{props.task.title}</h5>
                <h6 className="card-subtitle mb-2 text-muted">{props.task.course}</h6>
                <h6 className="card-subtitle mb-2 text-muted">{props.task.deadline.substring(0,10)}</h6>
                <p className="card-text">{props.task.description}</p>
                <input className="btn btn-outline-success btn-sm" value="Done" />
                <div className="card-body">
                    <a href="#" className="card-link">Edit</a>
                    <a href="#" className="card-link">Delete</a>
                </div>

            </div>
        </div>
    );
}

export default class ActiveTasksList extends Component {
    constructor(props){
        super(props);

        this.state = {
            isLoaded: false,
            tasks: []
        };
    }


    componentDidMount(){
        axios.get('http://localhost:5000/tasks/')
            .then(response => {
                this.setState({
                    isLoaded: true,
                    tasks: response.data 
                });
            })
            .catch(err => {
                console.log(err);
            });
    }

    taskList(){
        return this.state.tasks.map(currentTask => {
            return <Task task={currentTask} key={currentTask._id} />;
        });
    }

    render(){
        const {isLoaded, tasks} = this.state;

        if(!isLoaded){
            return (
                <div className="container">
                    Loading...
                </div>
            );
        }

        return (
            <div className="main-container">
                <div className="row">
                    {tasks.map(currentTask => <Task key={currentTask._id} task={currentTask} />)}
                </div>
            </div>
        );
    }

}
